import React, { useState, useRef, useEffect, useCallback } from 'react';
import { 
  Box, 
  Typography, 
  Button, 
  Paper, 
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Alert,
  CircularProgress,
  Grid,
  Card,
  CardContent,
  Container,
  Divider,
  Chip,
  Tooltip,
  Fade,
  Zoom,
  useTheme
} from '@mui/material';
import { styled } from '@mui/material/styles';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import DownloadIcon from '@mui/icons-material/Download';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import PeopleIcon from '@mui/icons-material/People';
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import { salaryService } from '../services/api';
import { isDemoMode } from '../context/AuthContext';

// Styled components
const DashboardContainer = styled(Box)(({ theme }) => ({
  minHeight: 'calc(100vh - 64px)',
  background: 'linear-gradient(135deg, rgba(25, 118, 210, 0.05) 0%, rgba(156, 39, 176, 0.05) 100%)',
  padding: theme.spacing(3),
}));

const PageHeader = styled(Box)(({ theme }) => ({
  marginBottom: theme.spacing(4),
  display: 'flex',
  flexDirection: 'column',
  [theme.breakpoints.up('md')]: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
}));

const StyledCard = styled(Card)(({ theme }) => ({
  borderRadius: '16px',
  boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
  height: '100%',
  transition: 'all 0.3s ease',
  '&:hover': {
    transform: 'translateY(-5px)',
    boxShadow: '0 12px 40px rgba(0, 0, 0, 0.15)',
  },
}));

const MetricCard = styled(StyledCard)(({ theme, color = 'primary' }) => ({
  background: `linear-gradient(135deg, ${theme.palette[color].light} 0%, ${theme.palette[color].main} 100%)`,
  color: 'white',
}));

const UploadCard = styled(StyledCard)(({ theme }) => ({
  background: 'white',
  overflow: 'hidden',
  position: 'relative',
  '&::after': {
    content: '""',
    position: 'absolute',
    bottom: 0,
    right: 0,
    width: '30%',
    height: '30%',
    background: 'linear-gradient(135deg, transparent 0%, rgba(25, 118, 210, 0.05) 100%)',
    borderTopLeftRadius: '50%',
  },
}));

const FileUploadArea = styled(Box)(({ theme, isDragging }) => ({
  border: `2px dashed ${isDragging ? theme.palette.primary.main : '#ccc'}`,
  borderRadius: '12px',
  padding: theme.spacing(4),
  textAlign: 'center',
  backgroundColor: isDragging ? 'rgba(25, 118, 210, 0.05)' : '#f9f9f9',
  margin: theme.spacing(2, 0, 3),
  cursor: 'pointer',
  transition: 'all 0.3s ease',
  '&:hover': {
    borderColor: theme.palette.primary.main,
    backgroundColor: 'rgba(25, 118, 210, 0.05)',
  },
}));

const ActionButton = styled(Button)(({ theme }) => ({
  borderRadius: '28px',
  padding: theme.spacing(1.2, 3),
  textTransform: 'none',
  fontWeight: 600,
  boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
  transition: 'all 0.3s ease',
  '&:hover': {
    transform: 'translateY(-2px)',
    boxShadow: '0 6px 16px rgba(0, 0, 0, 0.15)',
  },
}));

const StyledTableContainer = styled(TableContainer)(({ theme }) => ({
  borderRadius: '12px',
  boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)',
  '.MuiTableCell-head': {
    backgroundColor: theme.palette.primary.main,
    color: 'white',
    fontWeight: 600,
  },
  '.MuiTableRow-root': {
    '&:nth-of-type(odd)': {
      backgroundColor: 'rgba(0, 0, 0, 0.02)',
    },
    '&:hover': {
      backgroundColor: 'rgba(25, 118, 210, 0.05)',
    },
  },
}));

const Dashboard = () => {
  const [file, setFile] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [salaryResults, setSalaryResults] = useState([]);
  const [processedData, setProcessedData] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [fadeIn, setFadeIn] = useState(false);
  const fileInputRef = useRef(null);
  const theme = useTheme();

  useEffect(() => {
    // Trigger fade-in animation after component mounts
    setFadeIn(true);
  }, []);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      // Check if the file is an Excel file
      if (
        !selectedFile.name.endsWith('.xls') && 
        !selectedFile.name.endsWith('.xlsx')
      ) {
        setError('Please upload an Excel file (.xls or .xlsx)');
        setFile(null);
        return;
      }

      setError('');
      setFile(selectedFile);
    }
  };

  const handleFileUploadClick = () => {
    fileInputRef.current.click();
  };

  // Drag and drop handlers
  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);

    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      const droppedFile = e.dataTransfer.files[0];

      // Check if the file is an Excel file
      if (
        !droppedFile.name.endsWith('.xls') && 
        !droppedFile.name.endsWith('.xlsx')
      ) {
        setError('Please upload an Excel file (.xls or .xlsx)');
        return;
      }

      setError('');
      setFile(droppedFile);
    }
  };

  // Mock data for demo mode
  const mockSalaryData = {
    employeeResults: [
      { id: 1, name: 'John Doe', expectedHours: 160, actualHours: 165, baseSalary: 5000, calculatedSalary: 5156.25 },
      { id: 2, name: 'Jane Smith', expectedHours: 160, actualHours: 155, baseSalary: 4800, calculatedSalary: 4650 },
      { id: 3, name: 'Mike Johnson', expectedHours: 160, actualHours: 152, baseSalary: 5200, calculatedSalary: 4940 },
      { id: 4, name: 'Sarah Williams', expectedHours: 160, actualHours: 168, baseSalary: 5500, calculatedSalary: 5775 },
      { id: 5, name: 'David Brown', expectedHours: 160, actualHours: 140, baseSalary: 4700, calculatedSalary: 4112.5 }
    ],
    metrics: {
      totalEmployees: 5,
      totalPayout: 24633.75,
      averageSalary: 4926.75,
      lateMarks: 2
    }
  };

  // Auto-load mock data in demo mode
  useEffect(() => {
    if (isDemoMode) {
      // Simulate loading delay
      const timer = setTimeout(() => {
        setSalaryResults(mockSalaryData.employeeResults);
        setProcessedData(mockSalaryData.metrics);
      }, 1000);
      return () => clearTimeout(timer);
    }
  }, []);

  const handleProcessSalary = async () => {
    if (!file && !isDemoMode) {
      setError('Please select a file first');
      return;
    }

    try {
      setLoading(true);
      setError('');

      // Process salary data
      if (isDemoMode) {
        // Use mock data in demo mode
        setTimeout(() => {
          setSalaryResults(mockSalaryData.employeeResults);
          setProcessedData(mockSalaryData.metrics);
          setLoading(false);
        }, 1500); // Simulate API delay
      } else {
        // Normal mode - call the API
        const results = await salaryService.processSalary(file);
        setSalaryResults(results);

        // Calculate summary data
        const totalEmployees = results.length;
        const totalSalary = results.reduce((sum, item) => sum + item.finalPayableSalary, 0);
        const averageSalary = totalSalary / totalEmployees;
        const totalLateMarks = results.reduce((sum, item) => sum + item.lateMarks, 0);

        setProcessedData({
          totalEmployees,
          totalSalary,
          averageSalary,
          totalLateMarks
        });
      }

    } catch (err) {
      console.error('Error processing salary:', err);
      setError(
        err.response?.data?.message || 
        'Failed to process salary data. Please check your file format.'
      );
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadPDFs = async () => {
    if (!file && !isDemoMode) {
      setError('Please select a file first');
      return;
    }

    try {
      setLoading(true);
      setError('');

      // Generate and download PDF salary slips
      if (isDemoMode) {
        // Use mock data in demo mode
        setTimeout(() => {
          setLoading(false);
        }, 1500); // Simulate API delay
      } else {
        // Normal mode - call the API
        await salaryService.generatePDFs(file);
      }

    } catch (err) {
      console.error('Error generating PDFs:', err);
      setError(
        err.response?.data?.message || 
        'Failed to generate PDF salary slips. Please try again.'
      );
    } finally {
      setLoading(false);
    }
  };

  // Format currency
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 2
    }).format(amount);
  };

  // Format percentage
  const formatPercentage = (value) => {
    return (value * 100).toFixed(2) + '%';
  };

  // Get status color based on coefficient
  const getStatusColor = (coefficient) => {
    if (coefficient >= 0.95) return 'success';
    if (coefficient >= 0.85) return 'warning';
    return 'error';
  };

  // Get status label based on coefficient
  const getStatusLabel = (coefficient) => {
    if (coefficient >= 0.95) return 'Excellent';
    if (coefficient >= 0.85) return 'Good';
    if (coefficient >= 0.75) return 'Average';
    return 'Below Average';
  };

  return (
    <DashboardContainer>
      <Fade in={fadeIn} timeout={800}>
        <Container maxWidth="xl">
          <PageHeader>
            <Box>
              <Typography variant="h4" component="h1" fontWeight="700" color="primary.main">
                Salary Processor Dashboard
              </Typography>
              <Typography variant="body1" color="text.secondary">
                Upload, process and manage employee salary data
              </Typography>
            </Box>

            {processedData && (
              <Chip 
                label={`Month: ${new Date().toLocaleString('default', { month: 'long', year: 'numeric' })}`}
                color="primary" 
                variant="outlined"
                sx={{ fontWeight: 600, borderRadius: '16px', mt: { xs: 2, md: 0 } }}
              />
            )}
          </PageHeader>

          {error && (
            <Fade in={!!error} timeout={500}>
              <Alert 
                severity="error" 
                variant="filled"
                sx={{ mb: 3, borderRadius: '12px' }}
                onClose={() => setError('')}
              >
                {error}
              </Alert>
            </Fade>
          )}

          <Grid container spacing={3}>
            <Grid item xs={12} md={5} lg={4}>
              <Zoom in={fadeIn} timeout={800}>
                <UploadCard elevation={3}>
                  <CardContent sx={{ p: 3 }}>
                    <Typography variant="h6" fontWeight="600" gutterBottom>
                      Upload Attendance Data
                    </Typography>
                    <Divider sx={{ mb: 2 }} />

                    <FileUploadArea 
                      onClick={handleFileUploadClick}
                      onDragOver={handleDragOver}
                      onDragLeave={handleDragLeave}
                      onDrop={handleDrop}
                      isDragging={isDragging}
                    >
                      <input
                        type="file"
                        accept=".xls,.xlsx"
                        onChange={handleFileChange}
                        hidden
                        ref={fileInputRef}
                      />

                      <CloudUploadIcon 
                        sx={{ 
                          fontSize: 64, 
                          color: isDragging ? 'primary.main' : 'primary.light', 
                          mb: 2,
                          transition: 'all 0.3s ease'
                        }} 
                      />

                      <Typography variant="h6" gutterBottom color={isDragging ? 'primary.main' : 'inherit'}>
                        {file ? file.name : 'Drag & Drop or Click to Upload'}
                      </Typography>

                      <Typography variant="body2" color="text.secondary">
                        Upload monthly attendance Excel file (.xls or .xlsx)
                      </Typography>
                    </FileUploadArea>

                    <Grid container spacing={2}>
                      <Grid item xs={12} sm={6}>
                        <ActionButton
                          variant="contained"
                          color="primary"
                          fullWidth
                          onClick={handleProcessSalary}
                          disabled={!file || loading}
                          startIcon={loading ? <CircularProgress size={20} /> : <PlayArrowIcon />}
                        >
                          Process Data
                        </ActionButton>
                      </Grid>

                      <Grid item xs={12} sm={6}>
                        <ActionButton
                          variant="outlined"
                          color="primary"
                          fullWidth
                          onClick={handleDownloadPDFs}
                          disabled={!file || loading}
                          startIcon={<DownloadIcon />}
                        >
                          Download PDFs
                        </ActionButton>
                      </Grid>
                    </Grid>
                  </CardContent>
                </UploadCard>
              </Zoom>
            </Grid>

            {processedData && (
              <Grid item xs={12} md={7} lg={8}>
                <Zoom in={!!processedData} timeout={500}>
                  <Grid container spacing={3}>
                    <Grid item xs={12} sm={6} lg={3}>
                      <MetricCard color="primary">
                        <CardContent sx={{ p: 3 }}>
                          <Box display="flex" alignItems="center" mb={1}>
                            <PeopleIcon sx={{ mr: 1, opacity: 0.8 }} />
                            <Typography variant="body2" sx={{ opacity: 0.8 }}>
                              Employees
                            </Typography>
                          </Box>
                          <Typography variant="h4" fontWeight="600">
                            {processedData.totalEmployees}
                          </Typography>
                        </CardContent>
                      </MetricCard>
                    </Grid>

                    <Grid item xs={12} sm={6} lg={3}>
                      <MetricCard color="secondary">
                        <CardContent sx={{ p: 3 }}>
                          <Box display="flex" alignItems="center" mb={1}>
                            <AccountBalanceWalletIcon sx={{ mr: 1, opacity: 0.8 }} />
                            <Typography variant="body2" sx={{ opacity: 0.8 }}>
                              Total Payout
                            </Typography>
                          </Box>
                          <Typography variant="h4" fontWeight="600">
                            {formatCurrency(processedData.totalSalary)}
                          </Typography>
                        </CardContent>
                      </MetricCard>
                    </Grid>

                    <Grid item xs={12} sm={6} lg={3}>
                      <MetricCard color="success">
                        <CardContent sx={{ p: 3 }}>
                          <Box display="flex" alignItems="center" mb={1}>
                            <TrendingUpIcon sx={{ mr: 1, opacity: 0.8 }} />
                            <Typography variant="body2" sx={{ opacity: 0.8 }}>
                              Average Salary
                            </Typography>
                          </Box>
                          <Typography variant="h4" fontWeight="600">
                            {formatCurrency(processedData.averageSalary)}
                          </Typography>
                        </CardContent>
                      </MetricCard>
                    </Grid>

                    <Grid item xs={12} sm={6} lg={3}>
                      <MetricCard color="warning">
                        <CardContent sx={{ p: 3 }}>
                          <Box display="flex" alignItems="center" mb={1}>
                            <AccessTimeIcon sx={{ mr: 1, opacity: 0.8 }} />
                            <Typography variant="body2" sx={{ opacity: 0.8 }}>
                              Late Marks
                            </Typography>
                          </Box>
                          <Typography variant="h4" fontWeight="600">
                            {processedData.totalLateMarks}
                          </Typography>
                        </CardContent>
                      </MetricCard>
                    </Grid>
                  </Grid>
                </Zoom>
              </Grid>
            )}
          </Grid>

          {salaryResults.length > 0 && (
            <Fade in={salaryResults.length > 0} timeout={1000}>
              <Box sx={{ mt: 4 }}>
                <Typography variant="h5" fontWeight="600" color="text.primary" gutterBottom>
                  Salary Results
                </Typography>
                <StyledTableContainer component={Paper}>
                  <Table aria-label="salary results table">
                    <TableHead>
                      <TableRow>
                        <TableCell>Employee ID</TableCell>
                        <TableCell>Name</TableCell>
                        <TableCell align="right">Monthly Salary</TableCell>
                        <TableCell align="right">Hours (Expected/Actual)</TableCell>
                        <TableCell align="right">Coefficient</TableCell>
                        <TableCell align="right">Late Marks</TableCell>
                        <TableCell align="right">Final Salary</TableCell>
                        <TableCell align="center">Status</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {salaryResults.map((row) => {
                        const statusColor = getStatusColor(row.coefficient);

                        return (
                          <TableRow key={row.employeeId}>
                            <TableCell component="th" scope="row">
                              {row.employeeId}
                            </TableCell>
                            <TableCell>
                              <Typography fontWeight="500">{row.employeeName}</Typography>
                            </TableCell>
                            <TableCell align="right">{formatCurrency(row.monthlySalary)}</TableCell>
                            <TableCell align="right">
                              <Tooltip title={`Expected: ${row.expectedHours.toFixed(1)}, Actual: ${row.actualWorkedHours.toFixed(1)}`}>
                                <span>{row.actualWorkedHours.toFixed(1)} / {row.expectedHours.toFixed(1)}</span>
                              </Tooltip>
                            </TableCell>
                            <TableCell align="right">
                              <Typography fontWeight="500" color={`${statusColor}.main`}>
                                {formatPercentage(row.coefficient)}
                              </Typography>
                            </TableCell>
                            <TableCell align="right">{row.lateMarks}</TableCell>
                            <TableCell align="right">
                              <Typography fontWeight="700">
                                {formatCurrency(row.finalPayableSalary)}
                              </Typography>
                            </TableCell>
                            <TableCell align="center">
                              <Chip 
                                label={getStatusLabel(row.coefficient)}
                                color={statusColor}
                                size="small"
                                sx={{ fontWeight: 500 }}
                              />
                            </TableCell>
                          </TableRow>
                        );
                      })}
                    </TableBody>
                  </Table>
                </StyledTableContainer>
              </Box>
            </Fade>
          )}
        </Container>
      </Fade>
    </DashboardContainer>
  );
};

export default Dashboard;
