import React, { createContext, useState, useContext, useEffect } from 'react';
import jwt_decode from 'jwt-decode';

// Demo mode flag - set to true to enable demo without backend
const DEMO_MODE = true;

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if user is already logged in
    const token = localStorage.getItem('token');
    if (token) {
      try {
        // Verify token expiration
        const decodedToken = jwt_decode(token);
        const currentTime = Date.now() / 1000;

        if (decodedToken.exp > currentTime) {
          setCurrentUser(decodedToken);
          setIsAuthenticated(true);
        } else {
          // Token expired
          localStorage.removeItem('token');
        }
      } catch (error) {
        console.error('Invalid token', error);
        localStorage.removeItem('token');
      }
    }
    setLoading(false);
  }, []);

  const login = (token) => {
    // In demo mode, we'll accept the hardcoded credentials without a real token
    if (DEMO_MODE) {
      // Create a mock token with admin user info
      const mockToken = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiIxMjM0NTY3ODkwIiwibmFtZSI6IkFkbWluIFVzZXIiLCJyb2xlIjoiYWRtaW4iLCJpYXQiOjE1MTYyMzkwMjIsImV4cCI6MTY3MDAwOTQ1M30.Ipi4ReBG6SkjUPYKmVPZlswUwhUEXSUB4vVfN5-UY8s';
      localStorage.setItem('token', mockToken);
      setCurrentUser({
        sub: '1234567890',
        name: 'Admin User',
        role: 'admin'
      });
      setIsAuthenticated(true);
      return;
    }

    // Normal flow for when backend is available
    localStorage.setItem('token', token);
    const decodedToken = jwt_decode(token);
    setCurrentUser(decodedToken);
    setIsAuthenticated(true);
  };

  const logout = () => {
    localStorage.removeItem('token');
    setCurrentUser(null);
    setIsAuthenticated(false);
  };

  const value = {
    currentUser,
    isAuthenticated,
    loading,
    login,
    logout
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

// Export the demo mode flag to use in other components
export const isDemoMode = DEMO_MODE;
